import { Session } from "../models/Session";
import Stage from "../models/Stage";
import { SessionEvent } from "event-manager";

export default function AddStageMutator(session: Session, stageId: string) {
  const stage = session.emitPatch({
    op: "add",
    path: `/stages:::${stageId}`,
    value: {
      type: "class",
      className: "Stage"
    }
  });

  return stage;
}
