import { Session } from "../models/Session";
import Stage from "../models/Stage";
import { SessionEvent } from "event-manager";

export default function AddThumbnailStageMutator(
  session: Session,
  stageId: string
) {
  const stage = session.emitPatch({
    op: "add",
    path: `/thumbnailStages:::${stageId}`,
    value: {
      type: "class",
      className: "Stage"
    }
  });

  return stage;
}
