import { Session } from "../models/Session";
import { SessionEvent } from "event-manager";

export default function DuplicateRulersMutator(
  session: Session,
  originalFilePath: string,
  newFilePath: string
) {
  const oldFile = session.domain.files.get(originalFilePath);
  const newFile = session.domain.files.get(newFilePath);
  if (!oldFile || !newFile) {
    return;
  }

  const event = new SessionEvent("duplicateRulers", {
    oldFile,
    newFile
  });
  session.eventManager.dispatchEvent("duplicateRulers", event);
}
