import { Session } from "../models/Session";
import { SessionEvent } from "event-manager";

export default function FocusArtboardMutator(
  session: Session,
  filePath: string
) {
  // TODO: No need to persist, so we can emit SessionEvent here
  const file = session.domain.files.get(filePath);
  if (!file) {
    return;
  }
  const event = new SessionEvent("focusArtboard", {
    domainFile: file
  });

  session.eventManager.dispatchEvent("focusArtboard", event);
}
