import { Session } from "../models/Session";

export default function RemoveThumbnailStageMutator(
  session: Session,
  path: string
) {
  const thumbnailStage = session.thumbnailStages.get(path);
  if (thumbnailStage) {
    session.thumbnailStages.delete(path);
  }
  // const event = new SessionEvent("renameSymbolFile", {
  //   file,
  //   newName
  // });
  // session.eventManager.dispatchEvent("renameSymbolFile", event);
  session.domain.emit("thumbnailStages");
}
