import { IMutator } from "common-interfaces";
import { Session } from "../models/Session";
import { SessionEvent } from "event-manager";

export default function SetPathMutator(
  session: Session,
  type: "straight" | "disconnect"
) {
  // TODO: No need to persist, so we can emit SessionEvent here
  const event = new SessionEvent("setPath", {
    type
  });
  session.eventManager.dispatchEvent("setPath", event);
}
