import { Session } from "../models/Session";
import { SetFileDirtyMutator } from ".";
import { SessionEvent } from "event-manager";
import { Utils } from "utils";
import { Import, DomainLayer, FormControl } from "domain-x";
import { findIndex, isNil } from "lodash";

export default function SetScopeVariableMutator(
  session: Session,
  layerPath: string,
  key: string,
  value: any
) {
  const layer = session.getLayerFromLayerPath(layerPath);
  if (!layer) {
    return;
  }

  session.emitPatch({
    op: "replace",
    path: `/domain/files:::${Utils.modifyLayerPathForJsonPatch(
      layerPath
    )}/scopeVariables/${key}`,
    value: value
  });
}
