import { Session } from "../models/Session";
import { SessionEvent } from "event-manager";

export default function ShowCodeEditorMutator(session: Session) {
  session.showCodeEditor = true;
  session.emit("showCodeEditor");

  const event = new SessionEvent("showCodeEditor", {
    layers: session.getLayersFromSelectedLayerCollection()
  });
  session.eventManager.dispatchEvent("showCodeEditor", event);
}
