import * as React from "react";

import { Session } from "../../../../models/Session";

import ErrorMsgBox from "../../ErrorMessageBox";
import { Utils } from "utils";

type propType = {
  session: Session;
};
export default class ActivationFailed extends React.Component<propType, any> {
  reloadPage() {
    // window.parent.location.reload();
    Utils.reloadParentWindow();
  }
  quitApp() {
    this.props.session.drivers.appLifeCycleService.quitApp();
  }
  render() {
    return (
      <ErrorMsgBox
        errorHeading="Authentication Problem"
        errorMsg="Please check your internet connection or contact support if this issue persists."
        noInternet={this.reloadPage}
        quitApp={this.quitApp}
        buyNowBtn={false}
      />
    );
  }
}
