import * as React from "react";
import {
  PropertyGroup,
  PropertyHeader,
  Icon,
  FormCol,
  Button,
  Text
} from "components-x";

class CodeEditorPanel extends React.PureComponent<any, any> {
  render() {
    return (
      <PropertyGroup>
        <PropertyHeader onClick={this.props.onClick}>
          <Icon name="icon_codeeditor" size={14} />
          <FormCol>
            <Text uiSize="xs" bold uppercase letterSpacing>
              Code Editor
            </Text>
          </FormCol>
          <Button
            transparent={true}
            icon={
              <Icon
                name={this.props.showCodeEditor ? "downarrow" : "uparrow"}
                size={10}
              />
            }
            uiSize="xs"
          />
        </PropertyHeader>
      </PropertyGroup>
    );
  }
}

export default CodeEditorPanel;
