export default class Clipboard {
  type: "style" | "layer";
  data: any;

  set(clipboard: { data: any; type: "style" | "layer" }) {
    this.type = clipboard.type;
    this.data = clipboard.data;
  }

  get(): { data: any; type: "style" | "layer" } {
    return { type: this.type, data: this.data };
  }
}
