export default class FunctionRegistry {
  _functions: Map<string, Function>;
  constructor() {
    this._functions = new Map();
  }

  // Adding to the Map of functions
  addFunction = (name: string, callback: any) => {
    this._functions.set(name, callback);
  };

  // Executing the callback of the function with the given arguments
  executeFunction = (name: string, ...args: any[]) => {
    let fn = this._functions.get(name);
    if (fn) {
      return fn(...args);
    } else {
      throw new Error(`No function with the given name(${name}) found.`);
    }
  };

  // Removing the callback of the given function
  removeFunction = (name: string) => {
    this._functions.delete(name);
  };
}
