import BaseApiService from "./BaseApiService";
import { EnvConfig } from "common-interfaces";

export default class AnalyticsApiService {
  baseApiService: BaseApiService;
  constructor(config: EnvConfig) {
    this.baseApiService = new BaseApiService(config);
  }
  send(data: {
    uuid: string;
    version: string;
    actions: {
      action_name: string;
      action_path: string;
      action_time: string;
    }[];
  }) {
    return this.baseApiService.post("analytics", data);
  }
}
