export default class Model {
  internetAvailable: boolean;
  lastTimeInternetAvailable: string;
  constructor() {
    this.internetAvailable = false;
    this.lastTimeInternetAvailable = "";
  }
  isInternetAvailable(internetAvailable: boolean) {
    this.internetAvailable = internetAvailable;
  }
  setLastTimeInternetAvailable() {
    this.lastTimeInternetAvailable = Math.round(
      new Date().getTime() / 1000
    ).toString();
  }
}
