import BaseApiService from "./BaseApiService";

export default class UserApiService {
  baseApiService: BaseApiService;
  constructor(baseApiService: BaseApiService) {
    this.baseApiService = baseApiService;
  }
  register(formData: object) {
    return this.baseApiService.post("user/register", formData);
  }
  update(formData: object) {
    return this.baseApiService.post("user/update/version", formData);
  }
  verify(formData: object) {
    return this.baseApiService.post("user/verify", formData);
  }
  sketchConversionCount(body: object) {
    return this.baseApiService.post("user/list-add-ons", body);
  }
  updateSketchAddons(body: object) {
    return this.baseApiService.post("user/add-ons", body);
  }
  // static checkLicence(userData: object) {
  //   return BaseApiService.post("user/licence", userData);
  // }
}
