import { toast, cssTransition } from "react-toastify";
import NotificationBody from "./notificationBody";
import "./index.scss";

const Slide = cssTransition({
  enter: "slideIn",
  exit: "slideOut"
});
export default class NotificationManager {
  notifyError(msg: string | string[], msgTitle: string) {
    toast.error(NotificationBody(msg, msgTitle, "error"), {
      position: toast.POSITION.TOP_CENTER,
      hideProgressBar: true,
      autoClose: 5000,
      transition: Slide
    });
  }
  notifySuccess(msg: string | string[], msgTitle: string) {
    toast.success(NotificationBody(msg, msgTitle, "success"), {
      position: toast.POSITION.TOP_CENTER,
      hideProgressBar: true,
      autoClose: 5000,
      transition: Slide
    });
  }
  notifyWarning(msg: string | string[], msgTitle: string) {
    toast.info(NotificationBody(msg, msgTitle, "warning"), {
      position: toast.POSITION.TOP_CENTER,
      hideProgressBar: true,
      autoClose: 5000,
      transition: Slide
    });
  }
  notifyInfo(msg: string | string[], msgTitle: string) {
    toast.info(NotificationBody(msg, msgTitle, "info"), {
      position: toast.POSITION.TOP_CENTER,
      hideProgressBar: true,
      autoClose: 5000,
      transition: Slide
    });
  }
}
