import * as React from "react";
import { Icon } from "components-x";

export const CloseButton = (closeToast?: any) => (
  <div
    onClick={() => closeToast}
    style={{ cursor: "pointer" }}
    className="my-1"
  >
    <Icon name="icon-close" size={12} />
  </div>
);

class ToastContent extends React.Component<any, any> {
  render() {
    let icon = "icon_success";
    if (this.props.type === "error") {
      icon = "icon_error";
    } else if (this.props.type === "info") {
      icon = "icon_info_noti";
    } else if (this.props.type === "warning") {
      icon = "icon_alert";
    } else {
      icon = "icon_success";
    }
    return (
      <div
        style={{
          display: "flex"
        }}
        className="p-0"
      >
        <Icon name={icon} size={20} className="m-2" />
        <div className="py-2">
          <h4 className="notification-title">{this.props.title}</h4>
          {typeof this.props.msg === "object" ? (
            this.props.msg.map((element: string, index: string) => {
              return (
                <div key={index} style={{ padding: 4 }}>
                  <p>{element}</p>
                </div>
              );
            })
          ) : (
            <div className="py-2">
              <p style={{ lineHeight: 1.4 }}>{this.props.msg}</p>
            </div>
          )}
        </div>
      </div>
    );
  }
}

const ToastBody = (msg: string | string[], title: string, type?: string) => {
  return <ToastContent msg={msg} title={title} type={type} />;
};

export default ToastBody;
