import { Model } from "model";
import { types } from "serializable";
import { IRecentProject } from "common-interfaces";

export default class RecentProject extends Model implements IRecentProject {
  basePath: string = "";
  lastOpened: number = 0;

  setBasePath(basePath: string) {
    this.basePath = basePath;
  }
  setLastOpened(lastOpened: number) {
    this.lastOpened = lastOpened;
  }

  getFields(): { [key: string]: any } {
    return {
      basePath: types.literal,
      lastOpened: types.literal
    };
  }
}
