import { isEmpty } from "lodash";
import {
  getStyleName,
  getColor,
  getColorFill,
  getSVGStrokeWidth
} from "../style/styleUtils";
import {
  setMaskDataPoints,
  getMaskData,
  setMaskDataFrame,
  resetMaskData
} from "../../utils/NameMapsStore";
import { operateBooleanOnPointsArray } from "./../../utils/svgUtils";
import { convertPointsArray, getRadiusAdjustedPoints } from "conversions";

export default async function pathSVGCreate(
  layers: any,
  frame: any,
  style: any,
  scalingFactor: number,
  maskingDisplacement: any,
  parentObjId: string,
  hasClippingMask: boolean,
  parentFrame: any
) {
  if (isEmpty(layers)) {
    return {};
  }
  let child: any = {};
  let maskProp: Array<any> = [];
  let maskData: any = getMaskData();
  const strokeWidth = getSVGStrokeWidth(style, scalingFactor);
  const viewBox = `-${strokeWidth / 2} -${strokeWidth / 2} ${(!isEmpty(
    maskData.frame
  )
    ? maskData.frame.width
    : parentFrame.width) *
    scalingFactor +
    strokeWidth * 2} ${(!isEmpty(maskData.frame)
    ? maskData.frame.height
    : parentFrame.height) *
    scalingFactor +
    strokeWidth * 2}`;
  if (layers && layers.length === 1) {
    if (maskData.frame && !isEmpty(maskData.frame)) {
      maskingDisplacement = {
        x: frame.x - maskData.frame.x,
        y: frame.y - maskData.frame.y
      };
    }
    let points = getRadiusAdjustedPoints(
      convertPointsArray(
        layers[0].points,
        layers[0].frame,
        frame,
        maskingDisplacement,
        scalingFactor
      ),
      viewBox
    );
    if (!isEmpty(maskData.points)) {
      maskProp = [...maskData.points];
    }
    if (hasClippingMask) {
      setMaskDataPoints(points);
      setMaskDataFrame(frame);
    }
    child = getPathChild(
      layers[0],
      frame,
      style,
      points,
      scalingFactor,
      parentObjId
    );
  } else {
    try {
      let tempPoint: any = await operateBooleanOnPointsArray(
        layers,
        frame,
        scalingFactor,
        viewBox
      );
      let points: any = tempPoint;
      if (!isEmpty(maskData.points)) {
        maskProp = [...maskData.points];
      }
      if (hasClippingMask) {
        setMaskDataPoints(points);
        setMaskDataFrame(frame);
      }
      if (layers[0].shouldBreakMaskChain) {
        resetMaskData();
      }
      child = getPathChild(
        layers[0],
        frame,
        style,
        points,
        scalingFactor,
        parentObjId
      );
    } catch (error) {
      console.log(error, "Path Conversion error");
      return {};
    }
  }
  if (!isEmpty(maskProp)) {
    child.mask = maskProp;
  }

  child.props.viewBox = viewBox;
  return child;
}

function getPathChild(
  layer: any,
  frame: any,
  style: any,
  points: any,
  scalingFactor: number,
  parentObjId: string
) {
  let child: any = {
    type: "path",
    locked: false,
    styleName: getStyleName(layer.name),
    import: {
      layerName: "Path",
      packageName: "react-native-svg",
      isDefaultImport: false
    },
    template: {
      centerVertical: false,
      centerHorizontal: false
    },
    props: {
      strokeWidth: getSVGStrokeWidth(style, 2),
      fill: getColorFill(parentObjId, style),
      stroke:
        style.borders && style.borders.length > 0 && style.borders[0].isEnabled
          ? getColor(style.borders[0].color)
          : undefined,
      isClosed: layer.isClosed,
      points: points,
      style: {}
    },
    children: []
  };
  if (layer.isLocked) {
    child.locked = true;
  }
  // if (layer.rotation) {
  //   child.props.rotation = -layer.rotation;
  // }
  if (style && style.contextSettings && style.contextSettings.opacity) {
    child.props.fillOpacity = style.contextSettings.opacity;
    child.props.strokeOpacity = style.contextSettings.opacity;
  }
  return child;
}
