export const LayerTest = async (
  name: string,
  fileService: any,
  artboard: string,
  layerJSON: any
) => {
  let convertedJSON: any = await fileService.readFile(artboard);
  convertedJSON = JSON.parse(convertedJSON);
  expect(convertedJSON).toBeDefined();
  expect(convertedJSON.name).toBe(name);
  expect(convertedJSON.layer).toBeDefined();
  expect(convertedJSON.layer.children).toBeDefined();
  expect(convertedJSON.layer.children.length).toBeGreaterThan(0);
  expect(convertedJSON.layer.children[0]).toBeDefined();
  let convertedLayerJSON = convertedJSON.layer.children[0];
  expect(convertedLayerJSON).toMatchObject(layerJSON);
};
