import { getChangedName } from "./../utils/randomNameGenerator";
test("sketch-import-changed-artboard-name-test", () => {
  const newName1 = getChangedName("IPhone8", ["IPhone8", "IPhone7"]);
  expect(newName1).toBe("IPhone9");
  const newName2 = getChangedName("IPhone9", ["IPhone9"]);
  expect(newName2).toBe("IPhone10");
  const newName3 = getChangedName("Artboard", ["Artboard", "IPhone7"]);
  expect(newName3).toBe("Artboard1");
});
