import { isEmpty } from "lodash";
export default function canConvertToAvatar(internalLayers: any) {
  return (
    internalLayers &&
    !isEmpty(internalLayers) &&
    internalLayers[0]._class === "oval" &&
    internalLayers[0].frame &&
    Math.abs(internalLayers[0].frame.height - internalLayers[0].frame.width) < 2
  );
}
