import { Layer } from "./interfaces";
import getOverlapDirection from "./getOverlapDirection";
// import proximity from "./proximity";
import proximity from "./newProximity";
import enclosing from "./enclosing";
import { stacking, implementDFS } from "./stacking";
export default function grouping(layer: Layer, ipc: any): Layer {
  let transformedLayer;
  transformedLayer = enclosing(layer);
  transformedLayer = implementDFS(transformedLayer);
  transformedLayer = proximity(transformedLayer);
  return transformedLayer;
}
