import { Session } from "session";

function dynamicMenu(session: Session, event: any): Array<any> {
  return session.contextMenuItemCreators.map((fn: any) => fn(event));
}

export default function registerContextMenu(session: Session) {
  return (e: any) => {
    let items: any = dynamicMenu(session, e);
    return items;
  };
}
