import { Canvas, IPlugin, Layer } from '../..';
export default class ConstructionLinesPlugin implements IPlugin {
    name: string;
    canvas: Canvas;
    dispose: any;
    canvasDOMElement: HTMLCanvasElement;
    constructionLinesWrapper: HTMLDivElement;
    init(canvas: Canvas): void;
    activate(): void;
    attachListeners(layer: Layer): void;
    updateLines: () => void;
    deactivate(): void;
    showConstructionLines: () => void;
    setDimensions: () => void;
    hideConstructionLines: () => void;
}
