import { Canvas, IPlugin, Layer, IContext } from '../..';
export default class DataStore implements IPlugin {
    name: string;
    canvas: Canvas;
    dataMap: Map<Layer, any>;
    init(canvas: Canvas): void;
    activate(): void;
    setData(context: IContext, key: any, value: any): void;
    getData(context: IContext, key?: any): any;
    removeData(context: IContext, key?: any): void;
    deactivate(): void;
}
