import { Canvas, Layer, IPlugin } from '../..';
import { IDimensionWithRoot } from 'interfaces';
export default class FontResizerPlugin implements IPlugin {
    name: string;
    namespace: string;
    canvas: Canvas;
    disabled: boolean;
    init(canvas: Canvas): void;
    activate(): void;
    disableDrag(): void;
    isInsideCenterBox(layerDims: IDimensionWithRoot, parentDims: IDimensionWithRoot): boolean;
    getHandles: (next: any, layers: Array<Layer>) => any;
    handleDragStart: (next: any, data: any) => any;
    handleDrag: (next: any, data: any) => any;
    handleDragEnd: (next: any, data: any) => any;
    deactivate(): void;
}
