import { Canvas, IPlugin, Layer, Gradient } from '../..';
export default class GradientEditorPlugin implements IPlugin {
    name: string;
    nameSpace: string;
    gradient?: Gradient;
    activeIndex?: number;
    initialShift?: {
        x: number;
        y: number;
    };
    canvas: Canvas;
    dispose: any;
    isOpened: boolean;
    wrapperLayer?: any;
    init(canvas: Canvas): void;
    getScrollShift: () => {
        x: number;
        y: number;
    };
    render: (layer: Layer) => void;
    rerender: () => void;
    closeEditor: () => void;
    activate(): void;
    deactivate(): void;
}
