import { Canvas, IPlugin, Layer } from '../..';
export default class HoverPlugin implements IPlugin {
    name: string;
    canvas: Canvas;
    dispose: any;
    forbiddenLayers: Array<Layer>;
    hoverLayer: any;
    hoverLayers: Array<any>;
    hoverWrapperLayer: any;
    init(canvas: Canvas): void;
    hideLayerHover(context: any): void;
    showLayerHover(context: any): void;
    hideAllHoverBoxes(context: any): void;
    showAllHoverBoxes(context: any): void;
    showHover(layer: Layer): boolean;
    renderBoxes(): void;
    activate(): void;
    deactivate(): void;
}
