import { Canvas, IPlugin, PathEvent } from '../..';
export default class PathEditorPlugin implements IPlugin {
    name: string;
    nameSpace: string;
    canvas: Canvas;
    dispose: any;
    wrapperLayer: any;
    selectedTool: string;
    init(canvas: Canvas): void;
    handlePathEditorEvents: (event: any) => void;
    hide: () => void;
    getScrollShift: () => {
        x: number;
        y: number;
    };
    handleEvent: (data: PathEvent) => void;
    activate(): void;
    mountEditor(context: any, targetLayer: any): void;
    deactivate(): void;
}
