import { IPlugin, IContext, Canvas } from '../..';
import Config from './interfaces/Config';
export default class SnapPointsCalculator implements IPlugin {
    name: string;
    canvas: Canvas;
    config: Config;
    snapBound: number;
    init(canvas: Canvas): void;
    setSnapBound: (context: IContext, bound: any) => void;
    getSnapBound: (context: IContext) => number;
    getConfig: () => Config;
    setConfig: (context: IContext, config: Config) => void;
    activate(): void;
    deactivate(): void;
}
