import { IPlugin } from 'interfaces/IPlugin';
import { Canvas } from 'models/Canvas';
import DrawEvent from '../../plugins/DrawComponent/events/drawEvent';
import { Layer } from '../..';
export default class StageDesignerDrawComponent implements IPlugin {
    name: string;
    canvas: Canvas;
    selectedArtboard: Layer;
    firstTime: boolean;
    setSelectedArtboard: (data: DrawEvent) => void;
    drawShape: (data: any) => void;
    addListeners: () => void;
    getDimsFromArtboard: (layerPoints: {
        worldX: number;
        worldY: number;
        height: number;
        width: number;
    } | {}) => {
        x: number;
        y: number;
        height: any;
        width: any;
    };
    snapPoints: any;
    init(canvas: Canvas): void;
    activate(): void;
    deactivate(): void;
}
