    nextImgElement: any;
    currentMode: string;
    init(canvas: Canvas): void;
    activate(): void;
    previewMode(context: IContext): void;
    stageMode(context: IContext): void;
    nextArtboard(): void;
    previousArtboard(): void;
    setDisplayForButtons(): void;
    handleSelectedArtboardChange(): void;
    renderNextButton(): () => void;
    renderPreviousButton(): () => void;
    setPreviousButtonContainerLayout(): void;
    setNextButtonContainerLayout(): void;
    deactivate(): void;
