import { IContext, IDimension } from 'interfaces';
import ArtboardEvent from 'events/ArtboardEvent';
import { Canvas, IPlugin, Layer } from '../..';
export default class StatusBar implements IPlugin {
    name: string;
    canvas: Canvas;
    labeltext: any;
    wrapperDiv: any;
    deviceName: any;
    deviceOrientation: any;
    dispose: any;
    removeDom: any;
    statusBarMap: Map<any, any>;
    lastPageX: number;
    lastPageY: number;
    dragStart: any;
    dims: IDimension;
    ghostImageDOM: any;
    selectedArtboardChanged: (event: ArtboardEvent) => void;
    removeStatusBar(context: IContext): void;
    render: (layer: Layer) => void;
    addStatusBar(context: IContext, config: any): void;
    init(canvas: Canvas): void;
    activate(): void;
    deactivate(): void;
}
