import { Canvas, IPlugin, Layer } from '../..';
import { IContext } from 'interfaces';
export default class TextEditorPlugin implements IPlugin {
    name: string;
    canvas: Canvas;
    dispose: any;
    wrapperLayer: any;
    init(canvas: Canvas): void;
    mountTextEditor(context: IContext, layer: Layer): void;
    unmountTextEditor(context: IContext): void;
    activate(): void;
    deactivate(): void;
}
