export var imageWidget: {
    type: string;
    styleName: string;
    props: {
        style: {
            width: number;
            height: number;
            backgroundColor: string;
        };
    };
    children: {
        type: string;
        styleName: string;
        children: never[];
        props: {
            resizeMode: string;
            source: {
                uri: string;
            };
            style: {
                height: number;
                width: number;
            };
        };
    }[];
};
